import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import {
  Box,
  Grid,
  Paper,
  Typography,
  Avatar,
  Divider,
  List,
  ListItem,
  ListItemText,
  Chip,
  Tooltip,
  LinearProgress
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import VaccinesIcon from '@mui/icons-material/Vaccines';
import EventIcon from '@mui/icons-material/Event';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip as RechartsTooltip, Legend } from 'recharts';

const COLORS = ['#0088FE', '#00C49F'];

function Dashboard() {
  const [studentCount, setStudentCount] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [upcomingDrives, setUpcomingDrives] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/students/count')
      .then((res) => res.json())
      .then((data) => setStudentCount(data.totalStudents));

    fetch('http://localhost:8080/api/students/vaccinated/count')
      .then((res) => res.json())
      .then((data) => setVaccinatedCount(data.vaccinatedStudents));

    fetch('http://localhost:8080/api/vaccinationDrive/upcoming')
      .then((res) => res.json())
      .then((data) => setUpcomingDrives(data.upcomingVaccinationDrives));
  }, []);

  const vaccinationData = [
    { name: 'Vaccinated', value: vaccinatedCount },
    { name: 'Not Vaccinated', value: studentCount - vaccinatedCount }
  ];

  const formatClassChips = (classes) => {
    return classes?.map((cls, index) => (
      <Chip
        key={index}
        label={cls}
        size="small"
        sx={{ mr: 0.5, mb: 0.5 }}
        color="primary"
      />
    ));
  };

  return (
    <Box display="flex" height="100vh">
      <Sidebar />
      <Box flex={1} p={4} sx={{ backgroundColor: '#f9f9f9' }}>
        <Box mb={4} display="flex" alignItems="center" justifyContent="space-between">
          <Box display="flex" alignItems="center">
            <Avatar sx={{ bgcolor: '#1976d2', width: 56, height: 56 }}>
              <SchoolIcon fontSize="large" />
            </Avatar>
            <Box ml={2}>
              <Typography variant="h4" fontWeight="bold">Welcome Back!</Typography>
              <Typography variant="subtitle1" color="text.secondary">Here is your school health snapshot</Typography>
            </Box>
          </Box>
        </Box>

        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Paper elevation={3} sx={{ p: 3, borderRadius: 3 }}>
              <Box display="flex" alignItems="center" mb={2}>
                <SchoolIcon color="primary" sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h6">Total Students</Typography>
                  <Typography variant="h4" fontWeight="bold">{studentCount}</Typography>
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12} md={4}>
            <Paper elevation={3} sx={{ p: 3, borderRadius: 3 }}>
              <Box display="flex" alignItems="center" mb={2}>
                <VaccinesIcon color="success" sx={{ fontSize: 40, mr: 2 }} />
                <Box>
                  <Typography variant="h6">Vaccinated Students</Typography>
                  <Typography variant="h4" fontWeight="bold">{vaccinatedCount}</Typography>
                  <LinearProgress
                    variant="determinate"
                    value={(vaccinatedCount / studentCount) * 100 || 0}
                    sx={{ mt: 1, height: 10, borderRadius: 5 }}
                  />
                </Box>
              </Box>
            </Paper>
          </Grid>

          <Grid item xs={12} md={4}>
            <Paper elevation={3} sx={{ p: 3, borderRadius: 3 }}>
              <Box display="flex" alignItems="center" mb={2}>
                <EventIcon color="action" sx={{ fontSize: 40, mr: 2 }} />
                <Typography variant="h6">Upcoming Vaccination Drives</Typography>
              </Box>
              <Divider sx={{ mb: 1 }} />
              <List dense>
                {upcomingDrives.length === 0 ? (
                  <ListItem>
                    <ListItemText primary="No upcoming drives" />
                  </ListItem>
                ) : (
                  upcomingDrives.map((drive) => (
                    <ListItem key={drive.id} alignItems="flex-start">
                      <ListItemText
                        primary={<Typography fontWeight="bold">{new Date(drive.drive_date).toLocaleDateString()} | {drive.vaccine_name}</Typography>}
                        secondary={<>
                          <Typography variant="body2" color="text.secondary">Doses: {drive.doses_available}</Typography>
                          <Box mt={1} display="flex" flexWrap="wrap">{formatClassChips(drive.applicable_classes)}</Box>
                        </>}
                      />
                    </ListItem>
                  ))
                )}
              </List>
            </Paper>
          </Grid>

          <Grid item xs={12} md={6}>
            <Paper elevation={3} sx={{ p: 3, borderRadius: 3, height: 300 }}>
              <Typography variant="h6" gutterBottom>Vaccination Coverage</Typography>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={vaccinationData}
                    dataKey="value"
                    nameKey="name"
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    label
                  >
                    {vaccinationData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <RechartsTooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
}

export default Dashboard;
