import React, { useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Grid,
  Fade,
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [form, setForm] = useState({ username: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post('http://localhost:8080/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (err) {
      alert('Invalid credentials');
    }
  };

  return (
    <Grid container sx={{ minHeight: '100vh' }}>
      {/* Left side with background image + gradient overlay */}
      <Grid
        item
        xs={false}
        md={6}
        sx={{
          position: 'relative',
          backgroundImage:
            'url(https://www.fda.gov/files/styles/main_image_medium/public/iStock-157317886.jpg?itok=cIP_Y0sF)',
          backgroundRepeat: 'no-repeat',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <Box
          sx={{
            position: 'absolute',
            width: '100%',
            height: '100%',
            bgcolor: 'rgba(255,255,255,0.2)',
            backdropFilter: 'blur(3px)',
          }}
        />
      </Grid>

      {/* Right side with login form in glass card */}
      <Grid
        item
        xs={12}
        md={6}
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: '#f5f7fa',
        }}
      >
        <Fade in timeout={700}>
          <Paper
            elevation={6}
            sx={{
              p: 6,
              maxWidth: 400,
              width: '90%',
              borderRadius: 3,
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              backdropFilter: 'blur(8px)',
              boxShadow: '0 8px 32px rgba(0,0,0,0.15)',
            }}
          >
            <Box sx={{ textAlign: 'center', mb: 4 }}>
              <SchoolIcon color="primary" sx={{ fontSize: 50 }} />
              <Typography variant="h5" fontWeight="bold" mt={1}>
                School Vaccination Portal
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Secure login for school admins
              </Typography>
            </Box>

            <TextField
              label="Username"
              name="username"
              value={form.username}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Password"
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <Button
              variant="contained"
              fullWidth
              onClick={handleSubmit}
              sx={{
                mt: 3,
                py: 1.4,
                fontWeight: 'bold',
                background: 'linear-gradient(to right, #1976d2, #42a5f5)',
              }}
            >
              Login
            </Button>
          </Paper>
        </Fade>
      </Grid>
    </Grid>
  );
};

export default Login;
