// VaccinationDrivesPage.js
import React, { useEffect, useState } from 'react';
import {
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  CircularProgress,
  Box,
  Chip,
} from '@mui/material';
import axios from 'axios';
import dayjs from 'dayjs';

const VaccinationDrivesPage = () => {
  const [drives, setDrives] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDrives = async () => {
      try {
        const response = await axios.get('/api/vaccinationDrive');
        setDrives(response.data);
      } catch (err) {
        setError('Failed to load vaccination drives');
      } finally {
        setLoading(false);
      }
    };

    fetchDrives();
  }, []);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" mt={5}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Typography color="error" align="center" mt={5}>
        {error}
      </Typography>
    );
  }

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Vaccination Drives
      </Typography>

      <TableContainer component={Paper} elevation={3}>
        <Table>
          <TableHead sx={{ backgroundColor: '#e0f7fa' }}>
            <TableRow>
              <TableCell><strong>ID</strong></TableCell>
              <TableCell><strong>Vaccine Name</strong></TableCell>
              <TableCell><strong>Drive Date</strong></TableCell>
              <TableCell><strong>Doses Available</strong></TableCell>
              <TableCell><strong>Applicable Classes</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {drives.map((drive) => (
              <TableRow key={drive.id}>
                <TableCell>{drive.id}</TableCell>
                <TableCell>{drive.vaccine_name}</TableCell>
                <TableCell>{dayjs(drive.drive_date).format('DD MMM YYYY')}</TableCell>
                <TableCell>{drive.doses_available}</TableCell>
                <TableCell>
                  {drive.applicable_classes.map((cls, index) => (
                    <Chip
                      key={index}
                      label={cls}
                      size="small"
                      sx={{ mr: 0.5, mb: 0.5 }}
                      color="primary"
                      variant="outlined"
                    />
                  ))}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default VaccinationDrivesPage;
