import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import {
  Box,
  Typography,
  Paper,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Snackbar,
  Alert
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import axios from 'axios';

const Students = () => {
  const [students, setStudents] = useState([]);
  const [open, setOpen] = useState(false);
  const [editOpen, setEditOpen] = useState(false);
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [editStudent, setEditStudent] = useState(null);
  const [studentToDelete, setStudentToDelete] = useState(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  const [newStudent, setNewStudent] = useState({
    name: '',
    student_id: '',
    class: '',
    age: '',
    gender: '',
    vaccinated: '',
  });

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const handleChange = (e) => {
    setNewStudent({ ...newStudent, [e.target.name]: e.target.value });
  };

  const handleAddStudent = async () => {
    try {
      await axios.post('http://localhost:8080/api/students', newStudent);
      fetchStudents();
      setOpen(false);
      setNewStudent({ name: '', student_id: '', class: '', age: '', gender: '', vaccinated: '' });
      showSnackbar('Student added successfully!', 'success');
    } catch (err) {
      showSnackbar('Failed to add student.', 'error');
    }
  };

  const confirmDeleteStudent = (student) => {
    setStudentToDelete(student);
    setDeleteConfirmOpen(true);
  };

  const deleteStudent = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/students/${studentToDelete.id}`);
      fetchStudents();
      setDeleteConfirmOpen(false);
      setStudentToDelete(null);
      showSnackbar('Student deleted successfully!', 'success');
    } catch (err) {
      showSnackbar('Failed to delete student.', 'error');
    }
  };

  const openEditDialog = (student) => {
    setEditStudent(student);
    setEditOpen(true);
  };

  const handleEditChange = (e) => {
    setEditStudent({ ...editStudent, [e.target.name]: e.target.value });
  };

  const handleUpdateStudent = async () => {
    try {
      await axios.put(`http://localhost:8080/api/students/${editStudent.id}`, editStudent);
      fetchStudents();
      setEditOpen(false);
      showSnackbar('Student updated successfully!', 'success');
    } catch (err) {
      showSnackbar('Failed to update student.', 'error');
    }
  };

  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  return (
    <Box display="flex" height="100vh">
      <Sidebar />
      <Box sx={{ p: 4 }} flex={1}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
          <Typography variant="h5" fontWeight="bold">Student Details</Typography>
          <Button variant="contained" startIcon={<AddIcon />} onClick={() => setOpen(true)}>Add Student</Button>
        </Box>

        <TableContainer component={Paper}>
          <Table>
            <TableHead sx={{ backgroundColor: '#f5f5f5' }}>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Student ID</TableCell>
                <TableCell>Class</TableCell>
                <TableCell>Age</TableCell>
                <TableCell>Gender</TableCell>
                <TableCell>Covid19 Vaccinated?</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {students.map((student) => (
                <TableRow key={student.id}>
                  <TableCell>{student.name}</TableCell>
                  <TableCell>{student.student_id}</TableCell>
                  <TableCell>{student.class}</TableCell>
                  <TableCell>{student.age}</TableCell>
                  <TableCell>{student.gender}</TableCell>
                  <TableCell>{student.vaccinated}</TableCell>
                  <TableCell>
                    <Button size="small" onClick={() => openEditDialog(student)} sx={{ mr: 1 }}>Update</Button>
                    <Button size="small" color="error" onClick={() => confirmDeleteStudent(student)}>Delete</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Add Student Dialog */}
        <Dialog open={open} onClose={() => setOpen(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Add New Student</DialogTitle>
          <DialogContent>
            {['name', 'student_id', 'class', 'age', 'gender', 'vaccinated'].map((field) => (
              <TextField
                key={field}
                label={field === 'vaccinated' ? 'Covid19 Vaccinated?' : field.charAt(0).toUpperCase() + field.slice(1)}
                name={field}
                fullWidth
                margin="dense"
                value={newStudent[field]}
                onChange={handleChange}
              />
            ))}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpen(false)}>Cancel</Button>
            <Button onClick={handleAddStudent} variant="contained">Add</Button>
          </DialogActions>
        </Dialog>

        {/* Edit Student Dialog */}
        <Dialog open={editOpen} onClose={() => setEditOpen(false)} maxWidth="sm" fullWidth>
          <DialogTitle>Edit Student</DialogTitle>
          <DialogContent>
            {editStudent &&
              ['name', 'student_id', 'class', 'age', 'gender', 'vaccinated'].map((field) => (
                <TextField
                  key={field}
                  label={field === 'vaccinated' ? 'Covid19 Vaccinated?' : field.charAt(0).toUpperCase() + field.slice(1)}
                  name={field}
                  fullWidth
                  margin="dense"
                  value={editStudent[field]}
                  onChange={handleEditChange}
                />
              ))}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setEditOpen(false)}>Cancel</Button>
            <Button onClick={handleUpdateStudent} variant="contained">Update</Button>
          </DialogActions>
        </Dialog>

        {/* Delete Confirmation Dialog */}
        <Dialog open={deleteConfirmOpen} onClose={() => setDeleteConfirmOpen(false)}>
          <DialogTitle>Confirm Deletion</DialogTitle>
          <DialogContent>
            Are you sure you want to delete student <strong>{studentToDelete?.name}</strong>?
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteConfirmOpen(false)}>Cancel</Button>
            <Button color="error" variant="contained" onClick={deleteStudent}>Delete</Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={3000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert
            onClose={() => setSnackbar({ ...snackbar, open: false })}
            severity={snackbar.severity}
            sx={{ width: '100%' }}
          >
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Box>
    </Box>
  );
};

export default Students;
