import React, { useEffect, useState } from 'react';
import {
  Box,
  Button,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Paper,
  InputAdornment,
  IconButton,
  Grid,
  Card,
  CardContent,
  Typography,
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import SortIcon from '@mui/icons-material/Sort';
import { useNavigate } from 'react-router-dom';

function Students() {
  const [students, setStudents] = useState([]); // Ensure it's initialized as an empty array
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortBy, setSortBy] = useState('name');
  const navigate = useNavigate();

  useEffect(() => {
    fetchStudents();
  }, [page, rowsPerPage, sortBy, sortOrder]);

  const fetchStudents = async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/students?page=${page + 1}&limit=${rowsPerPage}&sortBy=${sortBy}&sortOrder=${sortOrder}&search=${search}`
      );
      const data = await response.json();
      setStudents(data.students || []); // Ensure that the value is an array even if no students are returned
    } catch (error) {
      console.error('Error fetching students:', error);
    }
  };

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
    setPage(0); // Reset to page 0 when searching
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleSort = (field) => {
    const isAsc = sortBy === field && sortOrder === 'asc';
    setSortBy(field);
    setSortOrder(isAsc ? 'desc' : 'asc');
  };

  return (
    <Box sx={{ p: 3 }}>
      <h2>Students</h2>

      <Grid container spacing={2}>
        <Grid item xs={12} sm={6} md={4}>
          <TextField
            label="Search by Name or ID"
            variant="outlined"
            fullWidth
            value={search}
            onChange={handleSearchChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <IconButton>
                    <SearchIcon />
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </Grid>
      </Grid>

      <Box sx={{ mt: 3 }}>
        <Card>
          <CardContent>
            <Typography variant="h6" component="div">
              Students List
            </Typography>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('name')}
                        startIcon={<SortIcon />}
                      >
                        Name
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('student_id')}
                        startIcon={<SortIcon />}
                      >
                        Student ID
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('class')}
                        startIcon={<SortIcon />}
                      >
                        Class
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('age')}
                        startIcon={<SortIcon />}
                      >
                        Age
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('gender')}
                        startIcon={<SortIcon />}
                      >
                        Gender
                      </Button>
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="text"
                        onClick={() => handleSort('vaccinated')}
                        startIcon={<SortIcon />}
                      >
                        Vaccinated
                      </Button>
                    </TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {students && students.length > 0 ? (
                    students.map((student) => (
                      <TableRow key={student.id} hover>
                        <TableCell>{student.name}</TableCell>
                        <TableCell>{student.student_id}</TableCell>
                        <TableCell>{student.class}</TableCell>
                        <TableCell>{student.age}</TableCell>
                        <TableCell>{student.gender}</TableCell>
                        <TableCell>{student.vaccinated}</TableCell>
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={6} align="center">
                        No students found
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>

            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={100} // You can replace this with the actual count from your API
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </CardContent>
        </Card>
      </Box>

      <Box sx={{ mt: 3 }}>
        <Button variant="contained" onClick={() => navigate('/students/add')}>
          Add Student
        </Button>
      </Box>
    </Box>
  );
}

export default Students;
