import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination,
  Button,
} from '@mui/material';
import { Download } from '@mui/icons-material';
import axios from 'axios';
import * as XLSX from 'xlsx';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

function Reports() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filter, setFilter] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);

  useEffect(() => {
    fetchReportData();
  }, []);

  const fetchReportData = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/reports');
      setData(response.data);
      setFilteredData(response.data);
    } catch (err) {
      console.error('Failed to fetch report data:', err);
    }
  };

  const handleFilterChange = (e) => {
    const value = e.target.value;
    setFilter(value);
    const filtered = data.filter((row) =>
      row.vaccine_name.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredData(filtered);
    setPage(0);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const exportToCSV = () => {
    const ws = XLSX.utils.json_to_sheet(filteredData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Reports');
    XLSX.writeFile(wb, 'vaccination_report.csv');
  };

  const exportToExcel = () => {
    const ws = XLSX.utils.json_to_sheet(filteredData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, 'Reports');
    XLSX.writeFile(wb, 'vaccination_report.xlsx');
  };

  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text('Vaccination Report', 14, 16);
    const tableData = filteredData.map((row) => [
      row.student_id,
      row.name,
      row.class,
      row.vaccinated ? 'Yes' : 'No',
      new Date(row.vaccination_date).toLocaleDateString(),
      row.vaccine_name,
    ]);

    doc.autoTable({
      startY: 20,
      head: [['Student ID', 'Name', 'Class', 'Vaccinated', 'Date', 'Vaccine']],
      body: tableData,
    });

    doc.save('vaccination_report.pdf');
  };

  return (
    <Box display="flex" height="100vh">
      <Sidebar />
      <Box p={4}>
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          Vaccination Reports
        </Typography>
        <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
          <TextField
            label="Filter by Vaccine Name"
            variant="outlined"
            fullWidth
            value={filter}
            onChange={handleFilterChange}
          />
        </Paper>
        <Paper elevation={3}>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Student ID</TableCell>
                  <TableCell>Name</TableCell>
                  <TableCell>Class</TableCell>
                  <TableCell>Vaccinated</TableCell>
                  <TableCell>Vaccination Date</TableCell>
                  <TableCell>Vaccine Name</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredData
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row) => (
                    <TableRow key={row.student_id}>
                      <TableCell>{row.student_id}</TableCell>
                      <TableCell>{row.name}</TableCell>
                      <TableCell>{row.class}</TableCell>
                      <TableCell>{row.vaccinated ? 'Yes' : 'No'}</TableCell>
                      <TableCell>
                        {new Date(row.vaccination_date).toLocaleDateString()}
                      </TableCell>
                      <TableCell>{row.vaccine_name}</TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>

          <TablePagination
            rowsPerPageOptions={[5, 10, 20]}
            component="div"
            count={filteredData.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={handleChangePage}
            onRowsPerPageChange={handleChangeRowsPerPage}
          />
        </Paper>

        <Box mt={3} display="flex" gap={2}>
          <Button variant="contained" startIcon={<Download />} onClick={exportToCSV}>
            Export CSV
          </Button>
          <Button variant="contained" startIcon={<Download />} onClick={exportToExcel}>
            Export Excel
          </Button>
          <Button variant="contained" startIcon={<Download />} onClick={exportToPDF}>
            Export PDF
          </Button>
        </Box>
      </Box>
    </Box>
  );
};

export default Reports;
