import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import {
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  CircularProgress,
  Box,
  Chip,
  TableSortLabel,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Button,
  IconButton,
} from '@mui/material';
import axios from 'axios';
import dayjs from 'dayjs';
import EditIcon from '@mui/icons-material/Edit';

const VaccinationDrivesPage = () => {
  const [drives, setDrives] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [sortDirection, setSortDirection] = useState('asc');
  const [sortBy, setSortBy] = useState('drive_date');
  const [openEditDialog, setOpenEditDialog] = useState(false);
  const [editDrive, setEditDrive] = useState(null);
  const [openAddDialog, setOpenAddDialog] = useState(false);
  const [newDrive, setNewDrive] = useState({
    vaccine_name: '',
    drive_date: '',
    doses_available: '',
    applicable_classes: '',
  });

  useEffect(() => {
    const fetchDrives = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/vaccinationDrive/upcoming');
        setDrives(response.data.upcomingVaccinationDrives);
      } catch (err) {
        console.error('Error fetching drives:', err);
        setError('Failed to load vaccination drives.');
      } finally {
        setLoading(false);
      }
    };

    fetchDrives();
  }, []);

  const handleSort = (property) => {
    const isAsc = sortBy === property && sortDirection === 'asc';
    setSortDirection(isAsc ? 'desc' : 'asc');
    setSortBy(property);
  };

  const handleEditClick = (drive) => {
    setEditDrive(drive);
    setOpenEditDialog(true);
  };

  const handleEditSave = async () => {
    try {
      await axios.put(`http://localhost:8080/api/vaccinationDrive/${editDrive.id}`, editDrive);
      setDrives((prevDrives) =>
        prevDrives.map((drive) => (drive.id === editDrive.id ? editDrive : drive))
      );
      setOpenEditDialog(false);
    } catch (error) {
      console.error('Error saving edit:', error);
    }
  };

  const handleAddClick = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/vaccinationDrive', newDrive);
      setDrives((prevDrives) => [...prevDrives, response.data]);
      setOpenAddDialog(false);
    } catch (error) {
      console.error('Error adding new drive:', error);
    }
  };

  const handleAddChange = (e) => {
    const { name, value } = e.target;
    setNewDrive((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditDrive((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCloseDialog = () => {
    setOpenEditDialog(false);
    setOpenAddDialog(false);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" height="100vh">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  const sortedDrives = drives.sort((a, b) => {
    if (sortDirection === 'asc') {
      return dayjs(a[sortBy]).isBefore(dayjs(b[sortBy])) ? -1 : 1;
    } else {
      return dayjs(a[sortBy]).isBefore(dayjs(b[sortBy])) ? 1 : -1;
    }
  });

  return (
    <Box display="flex" height="110vh">
      <Sidebar />
      <Box flex={1} p={4} sx={{ backgroundColor: '#f9f9f9' }}>
        <Typography variant="h4" gutterBottom>
          Vaccination Drives
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => setOpenAddDialog(true)}
          sx={{ marginBottom: 2 }}
        >
          Add New Drive
        </Button>
        <TableContainer component={Paper} elevation={3}>
          <Table>
            <TableHead sx={{ backgroundColor: '#e0f7fa' }}>
              <TableRow>
                <TableCell>
                  <TableSortLabel
                    active={sortBy === 'id'}
                    direction={sortDirection}
                    onClick={() => handleSort('id')}
                  >
                    ID
                  </TableSortLabel>
                </TableCell>
                <TableCell>
                  <TableSortLabel
                    active={sortBy === 'vaccine_name'}
                    direction={sortDirection}
                    onClick={() => handleSort('vaccine_name')}
                  >
                    Vaccine Name
                  </TableSortLabel>
                </TableCell>
                <TableCell>
                  <TableSortLabel
                    active={sortBy === 'drive_date'}
                    direction={sortDirection}
                    onClick={() => handleSort('drive_date')}
                  >
                    Drive Date
                  </TableSortLabel>
                </TableCell>
                <TableCell>Doses Available</TableCell>
                <TableCell>Applicable Classes</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedDrives.map((drive) => (
                <TableRow key={drive.id}>
                  <TableCell>{drive.id}</TableCell>
                  <TableCell>{drive.vaccine_name}</TableCell>
                  <TableCell>{dayjs(drive.drive_date).format('DD MMM YYYY')}</TableCell>
                  <TableCell>{drive.doses_available}</TableCell>
                  <TableCell>
                    {drive.applicable_classes.map((cls, index) => (
                      <Chip
                        key={index}
                        label={cls}
                        size="small"
                        sx={{ mr: 0.5, mb: 0.5 }}
                        color="primary"
                        variant="outlined"
                      />
                    ))}
                  </TableCell>
                  <TableCell>
                    <IconButton
                      color="primary"
                      onClick={() => handleEditClick(drive)}
                    >
                      <EditIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Edit Dialog */}
        <Dialog open={openEditDialog} onClose={handleCloseDialog}>
          <DialogTitle>Edit Vaccination Drive</DialogTitle>
          <DialogContent>
            <TextField
              label="Vaccine Name"
              fullWidth
              value={editDrive?.vaccine_name || ''}
              onChange={handleEditChange}
              name="vaccine_name"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Drive Date"
              fullWidth
              type="date"
              value={editDrive?.drive_date || ''}
              onChange={handleEditChange}
              name="drive_date"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Doses Available"
              fullWidth
              value={editDrive?.doses_available || ''}
              onChange={handleEditChange}
              name="doses_available"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Applicable Classes"
              fullWidth
              value={editDrive?.applicable_classes?.join(', ') || ''}
              onChange={handleEditChange}
              name="applicable_classes"
              sx={{ marginBottom: 2 }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Cancel</Button>
            <Button onClick={handleEditSave} color="primary">
              Save
            </Button>
          </DialogActions>
        </Dialog>

        {/* Add New Drive Dialog */}
        <Dialog open={openAddDialog} onClose={handleCloseDialog}>
          <DialogTitle>Add New Vaccination Drive</DialogTitle>
          <DialogContent>
            <TextField
              label="Vaccine Name"
              fullWidth
              value={newDrive.vaccine_name}
              onChange={handleAddChange}
              name="vaccine_name"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Drive Date"
              fullWidth
              type="date"
              value={newDrive.drive_date}
              onChange={handleAddChange}
              name="drive_date"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Doses Available"
              fullWidth
              value={newDrive.doses_available}
              onChange={handleAddChange}
              name="doses_available"
              sx={{ marginBottom: 2 }}
            />
            <TextField
              label="Applicable Classes"
              fullWidth
              value={newDrive.applicable_classes}
              onChange={handleAddChange}
              name="applicable_classes"
              sx={{ marginBottom: 2 }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Cancel</Button>
            <Button onClick={handleAddClick} color="primary">
              Add
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Box>
  );
};

export default VaccinationDrivesPage;
