import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Snackbar,
  Alert,
} from '@mui/material';
import { Delete, Edit } from '@mui/icons-material';
import Sidebar from '@/components/Sidebar';

const SchoolVaccinationDrives = () => {
  const [drives, setDrives] = useState([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [driveToDelete, setDriveToDelete] = useState(null);
  const [formData, setFormData] = useState({
    location: '',
    date: '',
    vaccineType: '',
  });

  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('success');

  useEffect(() => {
    fetchDrives();
  }, []);

  const fetchDrives = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/vaccinationDrive');
      setDrives(response.data);
    } catch (error) {
      console.error('Error fetching drives:', error);
    }
  };

  const handleOpenDialog = (drive = null) => {
    if (drive) {
      setFormData(drive);
      setIsEditing(true);
    } else {
      setFormData({ location: '', date: '', vaccineType: '' });
      setIsEditing(false);
    }
    setDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setDialogOpen(false);
    setFormData({ location: '', date: '', vaccineType: '' });
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      if (isEditing) {
        await axios.put(`http://localhost:8080/api/vaccinationDrive/${formData.id}`, formData);
        setDrives((prevDrives) =>
          prevDrives.map((d) => (d.id === formData.id ? formData : d))
        );
        setSnackbarMessage('Vaccination drive updated successfully.');
      } else {
        const response = await axios.post('http://localhost:8080/api/vaccinationDrive', formData);
        setDrives((prevDrives) => [...prevDrives, response.data]);
        setSnackbarMessage('Vaccination drive added successfully.');
      }
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      handleCloseDialog();
    } catch (error) {
      console.error('Error submitting form:', error);
      setSnackbarMessage('Failed to save the vaccination drive.');
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleDeleteClick = (drive) => {
    setDriveToDelete(drive);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/vaccinationDrive/${driveToDelete.id}`);
      setDrives((prevDrives) => prevDrives.filter((d) => d.id !== driveToDelete.id));
      setSnackbarMessage('Vaccination drive deleted successfully.');
      setSnackbarSeverity('success');
    } catch (error) {
      console.error('Error deleting drive:', error);
      setSnackbarMessage('Failed to delete the vaccination drive.');
      setSnackbarSeverity('error');
    } finally {
      setSnackbarOpen(true);
      setDeleteDialogOpen(false);
      setDriveToDelete(null);
    }
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box sx={{ flexGrow: 1, p: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4 }}>
          <Typography variant="h4" fontWeight="bold">Vaccination Drives</Typography>
          <Button variant="contained" onClick={() => handleOpenDialog()}>Add Drive</Button>
        </Box>
        <Grid container spacing={3}>
          {drives.map((drive) => (
            <Grid item xs={12} sm={6} md={4} key={drive.id}>
              <Card sx={{ position: 'relative', borderRadius: 3, boxShadow: 3 }}>
                <CardContent>
                  <Typography variant="h6">{drive.location}</Typography>
                  <Typography color="textSecondary">{drive.date}</Typography>
                  <Typography variant="body2">Vaccine: {drive.vaccineType}</Typography>
                  <Box sx={{ mt: 2 }}>
                    <IconButton onClick={() => handleOpenDialog(drive)}><Edit /></IconButton>
                    <IconButton onClick={() => handleDeleteClick(drive)}><Delete /></IconButton>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        {/* Add/Edit Dialog */}
        <Dialog open={dialogOpen} onClose={handleCloseDialog}>
          <DialogTitle>{isEditing ? 'Edit Drive' : 'Add Drive'}</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              name="location"
              label="Location"
              fullWidth
              value={formData.location}
              onChange={handleInputChange}
            />
            <TextField
              margin="dense"
              name="date"
              label="Date"
              type="date"
              fullWidth
              value={formData.date}
              onChange={handleInputChange}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              margin="dense"
              name="vaccineType"
              label="Vaccine Type"
              fullWidth
              value={formData.vaccineType}
              onChange={handleInputChange}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Cancel</Button>
            <Button onClick={handleSubmit} variant="contained">{isEditing ? 'Update' : 'Add'}</Button>
          </DialogActions>
        </Dialog>

        {/* Delete Confirmation Dialog */}
        <Dialog open={deleteDialogOpen} onClose={() => setDeleteDialogOpen(false)}>
          <DialogTitle>Confirm Delete</DialogTitle>
          <DialogContent>
            Are you sure you want to delete this vaccination drive?
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteDialogOpen(false)}>Cancel</Button>
            <Button onClick={confirmDelete} color="error">Delete</Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar Notification */}
        <Snackbar
          open={snackbarOpen}
          autoHideDuration={3000}
          onClose={() => setSnackbarOpen(false)}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert
            onClose={() => setSnackbarOpen(false)}
            severity={snackbarSeverity}
            sx={{ width: '100%' }}
          >
            {snackbarMessage}
          </Alert>
        </Snackbar>
      </Box>
    </Box>
  );
};

export default SchoolVaccinationDrives;
