import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Toolbar,
  Typography,
  Box,
  Divider,
  IconButton,
  Tooltip,
  useTheme,
} from '@mui/material';
import DashboardIcon from '@mui/icons-material/Dashboard';
import GroupIcon from '@mui/icons-material/Group';
import EventIcon from '@mui/icons-material/Event';
import BarChartIcon from '@mui/icons-material/BarChart';
import VaccinesIcon from '@mui/icons-material/Vaccines';
import MenuOpenIcon from '@mui/icons-material/MenuOpen';
import MenuIcon from '@mui/icons-material/Menu';

const Sidebar = () => {
  const location = useLocation();
  const [collapsed, setCollapsed] = useState(false);
  const theme = useTheme(); 

  const toggleDrawer = () => {
    setCollapsed(!collapsed);
  };

  const drawerWidth = collapsed ? 80 : 280;
  const menuItems = [
    { text: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
    { text: 'Students', icon: <GroupIcon />, path: '/students' },
    { text: 'Vaccination Drives', icon: <EventIcon />, path: '/drives' },
    { text: 'Reports', icon: <BarChartIcon />, path: '/reports' },
  ];

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        transition: 'width 0.3s ease',
        [`& .MuiDrawer-paper`]: {
          width: drawerWidth,
          boxSizing: 'border-box',
          backgroundColor: '#2c3e50',
          color: '#ecf0f1',
          borderRight: 'none',
          overflowX: 'hidden',
          transition: 'width 0.3s ease',
          boxShadow: theme.shadows[5],
        },
      }}
    >
      <Toolbar
        sx={{
          display: 'flex',
          justifyContent: collapsed ? 'center' : 'space-between',
          alignItems: 'center',
          px: 2,
          paddingTop: '20px',
        }}
      >
        {!collapsed && (
          <Box display="flex" alignItems="center" gap={1}>
            <VaccinesIcon sx={{ color: '#00bcd4' }} />
            <Typography variant="h5" noWrap fontWeight="bold" color="#ecf0f1">
              VaxSure
            </Typography>
          </Box>
        )}
        <IconButton onClick={toggleDrawer} sx={{ color: '#ecf0f1' }}>
          {collapsed ? <MenuIcon /> : <MenuOpenIcon />}
        </IconButton>
      </Toolbar>

      <Divider sx={{ borderColor: '#34495e', mb: 2 }} />

      <List>
        {menuItems.map((item) => {
          const isSelected = location.pathname === item.path;
          return (
            <Tooltip
              title={collapsed ? item.text : ''}
              placement="right"
              arrow
              key={item.text}
            >
              <ListItem
                button
                component={Link}
                to={item.path}
                selected={isSelected}
                sx={{
                  color: '#ecf0f1',
                  transition: 'all 0.3s ease',
                  borderRadius: '8px',
                  mx: 1,
                  my: 0.5,
                  '&.Mui-selected': {
                    backgroundColor: '#34495e',
                    '&:hover': {
                      backgroundColor: '#1e2a38',
                    },
                  },
                  '&:hover': {
                    backgroundColor: '#2e3d4f',
                    boxShadow: theme.shadows[3],
                  },
                }}
              >
                <ListItemIcon sx={{ color: '#00bcd4', minWidth: 50 }}>
                  {item.icon}
                </ListItemIcon>
                {!collapsed && (
                  <ListItemText
                    primary={item.text}
                    primaryTypographyProps={{
                      fontSize: '16px',
                      fontWeight: isSelected ? 'bold' : 'medium',
                    }}
                  />
                )}
              </ListItem>
            </Tooltip>
          );
        })}
      </List>
    </Drawer>
  );
};

export default Sidebar;
