import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  CircularProgress,
} from '@mui/material';
import axios from 'axios';
import Sidebar from '../components/Sidebar';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalStudents: null,
    vaccinatedStudents: null,
    upcomingDrives: null,
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [totalRes, vacRes, drivesRes] = await Promise.all([
          axios.get('http://localhost:8080/api/students/count'),
          axios.get('http://localhost:8080/api/students/vaccinated/count'),
          axios.get('http://localhost:8080/api/vaccinationDrive/upcoming'),
        ]);

        setStats({
          totalStudents: totalRes.data.totalStudents,
          vaccinatedStudents: vacRes.data.vaccinatedStudents,
          upcomingDrives: drivesRes.data.length,
        });
      } catch (err) {
        console.error('Error fetching dashboard stats', err);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 8 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />

      <Container sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" gutterBottom>
          Dashboard
        </Typography>

        <Grid container spacing={3}>
          {/* Total Students */}
          <Grid item xs={12} sm={6} md={4}>
            <Card sx={{ backgroundColor: '#e3f2fd' }}>
              <CardContent>
                <Typography variant="h6">Total Students</Typography>
                <Typography variant="h4">
                  {stats.totalStudents}
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          {/* Vaccinated Students */}
          <Grid item xs={12} sm={6} md={4}>
            <Card sx={{ backgroundColor: '#f1f8e9' }}>
              <CardContent>
                <Typography variant="h6">Vaccinated Students</Typography>
                <Typography variant="h4">
                  {stats.vaccinatedStudents}
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          {/* Upcoming Drives */}
          <Grid item xs={12} sm={6} md={4}>
            <Card sx={{ backgroundColor: '#fff3e0' }}>
              <CardContent>
                <Typography variant="h6">Upcoming Drives</Typography>
                <Typography variant="h4">
                  {stats.upcomingDrives}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
