import React, { useEffect, useState } from 'react';
import { Box, Grid, Typography, Paper, useTheme } from '@mui/material';
import Sidebar from '../components/Sidebar';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const Dashboard = () => {
  const [studentCount, setStudentCount] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [upcomingDrives, setUpcomingDrives] = useState([]);
  const theme = useTheme();

  useEffect(() => {
    fetch('http://localhost:8080/api/students/count')
      .then((res) => res.json())
      .then((data) => setStudentCount(data.totalStudents));

    fetch('http://localhost:8080/api/students/vaccinated/count')
      .then((res) => res.json())
      .then((data) => setVaccinatedCount(data.vaccinatedStudents));

    fetch('http://localhost:8080/api/vaccination-drives/upcoming')
      .then((res) => res.json())
      .then((data) => setUpcomingDrives(data));
  }, []);

  const vaccinationData = [
    { name: 'Vaccinated', value: vaccinatedCount },
    { name: 'Not Vaccinated', value: studentCount - vaccinatedCount },
  ];

  const COLORS = ['#4caf50', '#f44336'];

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar />
      <Box component="main" sx={{ flexGrow: 1, p: 4, backgroundColor: '#f4f6f8', minHeight: '100vh' }}>
        <Typography variant="h4" gutterBottom>
          Dashboard
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Paper elevation={3} sx={{ p: 3 }}>
              <Typography variant="h6">Total Students</Typography>
              <Typography variant="h4">{studentCount}</Typography>
            </Paper>
          </Grid>

          <Grid item xs={12} md={4}>
            <Paper elevation={3} sx={{ p: 3 }}>
              <Typography variant="h6">Vaccinated Students</Typography>
              <Typography variant="h4">{vaccinatedCount}</Typography>
            </Paper>
          </Grid>

          <Grid item xs={12}>
            <Paper elevation={3} sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom>
                Upcoming Vaccination Drives
              </Typography>
              {upcomingDrives.length === 0 ? (
                <Typography>No upcoming drives</Typography>
              ) : (
                <Grid container spacing={2}>
                  {upcomingDrives.map((drive) => (
                    <Grid item xs={12} md={6} lg={4} key={drive.id}>
                      <Paper sx={{ p: 2, backgroundColor: '#e3f2fd' }}>
                        <Typography variant="subtitle1">{drive.vaccine}</Typography>
                        <Typography variant="body2">Date: {drive.date}</Typography>
                        <Typography variant="body2">Slots: {drive.slots}</Typography>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              )}
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default Dashboard;
