import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';
import {
  Box,
  Typography,
  Paper,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Snackbar,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import axios from 'axios';
import Alert from '@mui/material/Alert';

const Students = () => {
  const [students, setStudents] = useState([]);
  const [open, setOpen] = useState(false);
  const [newStudent, setNewStudent] = useState({
    name: '',
    student_id: '',
    class: '',
    age: '',
    gender: '',
    vaccinated: '',
  });
  const [openRegisterDialog, setOpenRegisterDialog] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [drives, setDrives] = useState([]);
  const [selectedDrive, setSelectedDrive] = useState('');
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');

  useEffect(() => {
    fetchStudents();
    fetchVaccinationDrives();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/students');
      setStudents(res.data);
    } catch (err) {
      console.error('Error fetching students:', err);
    }
  };

  const fetchVaccinationDrives = async () => {
    try {
      const res = await axios.get('http://localhost:8080/api/drives');
      setDrives(res.data);
    } catch (err) {
      console.error('Error fetching vaccination drives:', err);
    }
  };

  const handleChange = (e) => {
    setNewStudent({ ...newStudent, [e.target.name]: e.target.value });
  };

  const handleAddStudent = async () => {
    try {
      await axios.post('http://localhost:8080/api/students', newStudent);
      fetchStudents();
      setOpen(false);
      setNewStudent({
        name: '',
        student_id: '',
        class: '',
        age: '',
        gender: '',
        vaccinated: '',
      });
    } catch (err) {
      console.error('Error adding student:', err);
    }
  };

  const handleRegisterClick = (student) => {
    setSelectedStudent(student);
    setOpenRegisterDialog(true);
  };

  const handleRegister = async () => {
    if (!selectedDrive) {
      setSnackbarMessage('Please select a vaccination drive');
      setSnackbarOpen(true);
      return;
    }

    try {
      await axios.post('http://localhost:8080/api/register', {
        studentId: selectedStudent.id,
        driveId: selectedDrive,
      });
      setSnackbarMessage('Student successfully registered to the drive');
      setSnackbarOpen(true);
      setOpenRegisterDialog(false);
      fetchStudents(); // refresh student data
    } catch (err) {
      setSnackbarMessage('Error registering student');
      setSnackbarOpen(true);
      console.error('Error registering student:', err);
    }
  };

  const handleUpdate = (student) => {
    // Open a dialog to update student details
    setNewStudent(student);
    setOpen(true);
  };

  const handleDelete = async (studentId) => {
    try {
      await axios.delete(`http://localhost:8080/api/students/${studentId}`);
      setSnackbarMessage('Student successfully deleted');
      setSnackbarOpen(true);
      fetchStudents(); // refresh student data
    } catch (err) {
      setSnackbarMessage('Error deleting student');
      setSnackbarOpen(true);
      console.error('Error deleting student:', err);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  return (
    <Box display="flex" height="100vh">
      <Sidebar />
      <Box sx={{ p: 4 }} flex={1}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
          <Typography variant="h5" fontWeight="bold">
            Student Details
          </Typography>
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => setOpen(true)}
          >
            Add Student
          </Button>
        </Box>

        <TableContainer component={Paper}>
          <Table>
            <TableHead sx={{ backgroundColor: '#f5f5f5' }}>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Student ID</TableCell>
                <TableCell>Class</TableCell>
                <TableCell>Age</TableCell>
                <TableCell>Gender</TableCell>
                <TableCell>Covid19 Vaccinated ?</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {students.map((student) => (
                <TableRow key={student.id}>
                  <TableCell>{student.name}</TableCell>
                  <TableCell>{student.student_id}</TableCell>
                  <TableCell>{student.class}</TableCell>
                  <TableCell>{student.age}</TableCell>
                  <TableCell>{student.gender}</TableCell>
                  <TableCell>{student.vaccinated}</TableCell>
                  <TableCell>
                    <Button size="small" color="error" onClick={() => handleDelete(student.id)}>Delete</Button>
                    <Button size="small" color="secondary" onClick={() => handleUpdate(student)}>Update</Button>
                    {student.vaccinated === 'No' && (
                      <Button size="small" color="primary" onClick={() => handleRegisterClick(student)}>Register</Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Add Student Modal */}
        <Dialog open={open} onClose={() => setOpen(false)} maxWidth="sm" fullWidth>
          <DialogTitle>{newStudent.id ? 'Update Student' : 'Add New Student'}</DialogTitle>
          <DialogContent>
            <TextField
              label="Name"
              name="name"
              fullWidth
              margin="dense"
              value={newStudent.name}
              onChange={handleChange}
            />
            <TextField
              label="Student ID"
              name="student_id"
              fullWidth
              margin="dense"
              value={newStudent.student_id}
              onChange={handleChange}
            />
            <TextField
              label="Class"
              name="class"
              fullWidth
              margin="dense"
              value={newStudent.class}
              onChange={handleChange}
            />
            <TextField
              label="Age"
              name="age"
              fullWidth
              margin="dense"
              value={newStudent.age}
              onChange={handleChange}
            />
            <TextField
              label="Gender"
              name="gender"
              fullWidth
              margin="dense"
              value={newStudent.gender}
              onChange={handleChange}
            />
            <TextField
              label="Covid19 Vaccinated ?"
              name="vaccinated"
              fullWidth
              margin="dense"
              value={newStudent.vaccinated}
              onChange={handleChange}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpen(false)}>Cancel</Button>
            <Button onClick={handleAddStudent} variant="contained">
              {newStudent.id ? 'Update' : 'Add'}
            </Button>
          </DialogActions>
        </Dialog>

        {/* Register Vaccination Drive Dialog */}
        <Dialog
          open={openRegisterDialog}
          onClose={() => setOpenRegisterDialog(false)}
          maxWidth="sm"
          fullWidth
        >
          <DialogTitle>Register Student to Vaccination Drive</DialogTitle>
          <DialogContent>
            <TextField
              select
              label="Select Vaccination Drive"
              fullWidth
              margin="dense"
              value={selectedDrive}
              onChange={(e) => setSelectedDrive(e.target.value)}
              SelectProps={{
                native: true,
              }}
            >
              <option value=""></option>
              {drives.map((drive) => (
                <option key={drive.id} value={drive.id}>
                  {drive.name} - {drive.date}
                </option>
              ))}
            </TextField>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenRegisterDialog(false)}>Cancel</Button>
            <Button onClick={handleRegister} variant="contained">
              Register
            </Button>
          </DialogActions>
        </Dialog>

        {/* Delete Confirmation Dialog */}
        <Dialog open={deleteConfirmOpen} onClose={() => setDeleteConfirmOpen(false)}>
          <DialogTitle>Confirm Deletion</DialogTitle>
          <DialogContent>
            Are you sure you want to delete student <strong>{studentToDelete?.name}</strong>?
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setDeleteConfirmOpen(false)}>Cancel</Button>
            <Button color="error" variant="contained" onClick={deleteStudent}>Delete</Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={3000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert
            onClose={() => setSnackbar({ ...snackbar, open: false })}
            severity={snackbar.severity}
            sx={{ width: '100%' }}
          >
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Box>
    </Box>
  );
};

export default Students;
