import React, { useEffect, useState } from 'react';
import Sidebar from '../components/Sidebar';

function Dashboard() {
  const [studentCount, setStudentCount] = useState(0);
  const [vaccinatedCount, setVaccinatedCount] = useState(0);
  const [upcomingDrives, setUpcomingDrives] = useState([]);

  // Simulate data fetch from API
  useEffect(() => {
    // Fetching student data (replace with actual API calls)
    setStudentCount(200);  // Placeholder for total students
    setVaccinatedCount(150);  // Placeholder for vaccinated students

    // Upcoming vaccination drives (replace with actual API call)
    setUpcomingDrives([
      { id: 1, date: '2025-05-15', vaccine: 'Vaccine A', slots: 50 },
      { id: 2, date: '2025-06-10', vaccine: 'Vaccine B', slots: 60 },
    ]);
  }, []);

  return (
    <div style={styles.container}>
      <Sidebar />
      <div style={styles.mainContent}>
        <h2>Dashboard</h2>
        <div style={styles.metrics}>
          <div style={styles.metric}>
            <h3>Total Students</h3>
            <p>{studentCount}</p>
          </div>
          <div style={styles.metric}>
            <h3>Vaccinated Students</h3>
            <p>{vaccinatedCount}</p>
          </div>
          <div style={styles.metric}>
            <h3>Upcoming Vaccination Drives</h3>
            {upcomingDrives.length === 0 ? (
              <p>No upcoming drives</p>
            ) : (
              <ul>
                {upcomingDrives.map((drive) => (
                  <li key={drive.id}>
                    {drive.date} - {drive.vaccine} ({drive.slots} slots)
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

// Container styles to support the Sidebar layout
const styles = {
  container: {
    display: 'flex',
    height: '100vh',
  },
  mainContent: {
    marginLeft: '250px', // Sidebar width
    padding: '20px',
    width: 'calc(100% - 250px)',
  },
  metrics: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: '20px',
  },
  metric: {
    backgroundColor: '#ecf0f1',
    padding: '20px',
    borderRadius: '8px',
    textAlign: 'center',
    width: '30%',
  },
};

export default Dashboard;
